import{_ as a,c as t,o as e,U as d,AK as c,AL as i}from"./chunks/framework.fU6PLAsS.js";const m=JSON.parse('{"title":"Rainbow x Icevan v1.1","description":"","frontmatter":{"layout":"doc","outline":"deep"},"headers":[],"relativePath":"technical_docs/device_icevan.md","filePath":"technical_docs/device_icevan.md"}'),v={name:"technical_docs/device_icevan.md"},n=d('<h1 id="rainbow-x-icevan-v1-1" tabindex="-1" data-v-89cf40e3>Rainbow x Icevan v1.1 <a class="header-anchor" href="#rainbow-x-icevan-v1-1" aria-label="Permalink to &quot;Rainbow x Icevan v1.1&quot;" data-v-89cf40e3>​</a></h1><h3 id="_1-configuration-setting" tabindex="-1" data-v-89cf40e3>1. Configuration &amp; Setting <a class="header-anchor" href="#_1-configuration-setting" aria-label="Permalink to &quot;1. Configuration &amp; Setting&quot;" data-v-89cf40e3>​</a></h3><p data-v-89cf40e3><img src="'+c+'" alt="missing" data-v-89cf40e3></p><ul data-v-89cf40e3><li data-v-89cf40e3>Connect a commercial USB-RS232 (USB-serial) device to the control box of Rainbow Robot</li><li data-v-89cf40e3>Communication lines between devices should be cross-connected / Connect the GND (ground) wires on both sides. (Common ground)</li><li data-v-89cf40e3>Icevan equipment uses serial communication speed/Baud rate as 9600 bps, so set the rainbow control box accordingly.</li></ul><p data-v-89cf40e3><img src="'+i+'" alt="missing" data-v-89cf40e3></p><h3 id="_2-variable-description" tabindex="-1" data-v-89cf40e3>2. Variable Description <a class="header-anchor" href="#_2-variable-description" aria-label="Permalink to &quot;2. Variable Description&quot;" data-v-89cf40e3>​</a></h3><div class="warning custom-block" data-v-89cf40e3><p class="custom-block-title" data-v-89cf40e3>WARNING</p><ul data-v-89cf40e3><li data-v-89cf40e3>In the table below, the information in the yellow area is the information transmitted by Icevan (ice machine).</li><li data-v-89cf40e3>The values stored in the variables in the yellow area contain the information that the Icevan transmits to the Rainbow-ControlBox through communication.</li></ul></div><table class="th-align" data-v-89cf40e3><tr data-v-89cf40e3><th data-v-89cf40e3>Variable Name</th><th data-v-89cf40e3>Description</th><th data-v-89cf40e3>Ref. Icevan Manual</th></tr><tr data-v-89cf40e3><td class="bg-none" data-v-89cf40e3>ICE_INFO_CONNECTED</td><td data-v-89cf40e3>Whether it is communicating with the Icevan</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td class="bg-none" data-v-89cf40e3>ICE_INFO_REQUESTING</td><td data-v-89cf40e3>*RB developer only</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td class="bg-none" data-v-89cf40e3>ICE_INFO_USING</td><td data-v-89cf40e3>*RB developer only</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td class="bg-none" data-v-89cf40e3>ICE_INFO_VECSIZE</td><td data-v-89cf40e3>*RB developer only</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_MODE_COMM</td><td data-v-89cf40e3>1:Communication mode / 0:Normal mode</td><td data-v-89cf40e3>Chap. 1</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_MODE_CUP</td><td data-v-89cf40e3>1:Ignore Cup depart / 0: Stop Ice-out if Cupdepart</td><td data-v-89cf40e3>Chap. 1</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_TIME_ICE</td><td data-v-89cf40e3>Last Ice-out time</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_TIME_WATER</td><td data-v-89cf40e3>Last Water-out time</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_AMBI_LOW</td><td data-v-89cf40e3>Lower limit for ambient temperature setting</td><td data-v-89cf40e3>Chap. 4</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_AMBI_HIGH</td><td data-v-89cf40e3>Upper limit for ambient temperature setting</td><td data-v-89cf40e3>Chap. 4</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_TEMP_AMBI</td><td data-v-89cf40e3>Temperature : Ambient</td><td data-v-89cf40e3>Chap. 5</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_TEMP_EVAPO</td><td data-v-89cf40e3>Temperature : Evaporator</td><td data-v-89cf40e3>Chap. 5-1</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_INFO_TEMP_CONDEN</td><td data-v-89cf40e3>Temperature : Condensor</td><td data-v-89cf40e3>Chap. 5-1</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_LAST_ICE_NO</td><td data-v-89cf40e3>If ice is not detected in the previous ice-out</td><td data-v-89cf40e3>Manual: ignore this</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_LAST_ICE_YES</td><td data-v-89cf40e3>If ice is detected in the previous ice-out</td><td data-v-89cf40e3>Manual: ignore this</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_COMP_WORK</td><td data-v-89cf40e3>Whether the compressor works (1=working)</td><td data-v-89cf40e3>Page 3 upper table</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_MOTOR_WORK</td><td data-v-89cf40e3>Whether the gear-motor works (1=working)</td><td data-v-89cf40e3>Page 3 upper table</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_OUT_SOL</td><td data-v-89cf40e3>Ice-out SOL detection (1=detected)</td><td data-v-89cf40e3>Page 3 upper table</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_CUP_LEVEL</td><td data-v-89cf40e3>Cup lever detection (1=detected)</td><td data-v-89cf40e3>Page 3 upper table</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_COMM_MODE</td><td data-v-89cf40e3>1 if communication mode</td><td data-v-89cf40e3>Page 3 upper table</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_FULL_ICE</td><td data-v-89cf40e3>Ice-Full signal (1 = Full Ice)</td><td data-v-89cf40e3>Page 3 upper table</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_ERR_1</td><td data-v-89cf40e3>*RB developer only</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_ERR_2</td><td data-v-89cf40e3>*RB developer only</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_ERR_3</td><td data-v-89cf40e3>*RB developer only</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_ERR_4</td><td data-v-89cf40e3>*RB developer only</td><td data-v-89cf40e3></td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_ERR_CODE</td><td data-v-89cf40e3>Error code (0 = Normal)</td><td data-v-89cf40e3>Page 3 lower table</td></tr><tr data-v-89cf40e3><td data-v-89cf40e3>ICE_STATE_RD</td><td data-v-89cf40e3>1=Ready to IceOut / 0=IceOut done or Timeout</td><td data-v-89cf40e3>Page 3 middle table</td></tr></table><h3 id="_3-function-description" tabindex="-1" data-v-89cf40e3>3. Function Description <a class="header-anchor" href="#_3-function-description" aria-label="Permalink to &quot;3. Function Description&quot;" data-v-89cf40e3>​</a></h3><br data-v-89cf40e3><h4 id="_3-1-icevan-update-start" tabindex="-1" data-v-89cf40e3>3.1 icevan_update_start(#) <a class="header-anchor" href="#_3-1-icevan-update-start" aria-label="Permalink to &quot;3.1 icevan_update_start(#)&quot;" data-v-89cf40e3>​</a></h4><ul data-v-89cf40e3><li data-v-89cf40e3><p data-v-89cf40e3>icevan_update_start(#)<br data-v-89cf40e3></p><p data-v-89cf40e3>Start requesting status/setting information from the ice machine</p><p data-v-89cf40e3>Input :</p><blockquote data-v-89cf40e3><p data-v-89cf40e3>1: Data request Start<br data-v-89cf40e3> 0: Data request End</p></blockquote><p data-v-89cf40e3>Example :</p><div class="language- vp-adaptive-theme" data-v-89cf40e3><button title="Copy Code" class="copy" data-v-89cf40e3></button><span class="lang" data-v-89cf40e3></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-89cf40e3><code data-v-89cf40e3><span class="line" data-v-89cf40e3><span data-v-89cf40e3>icevan_update_start(1)</span></span></code></pre></div></li></ul><h4 id="_3-2-icevan-set-mode" tabindex="-1" data-v-89cf40e3>3.2 icevan_set_mode(#, #) <a class="header-anchor" href="#_3-2-icevan-set-mode" aria-label="Permalink to &quot;3.2 icevan_set_mode(#, #)&quot;" data-v-89cf40e3>​</a></h4><ul data-v-89cf40e3><li data-v-89cf40e3><p data-v-89cf40e3>icevan_set_mode(#, #)<br data-v-89cf40e3></p><p data-v-89cf40e3>Select the operation mode of the ice machine (Ref. Icevan Manual Chap.1)</p><p data-v-89cf40e3>Input :</p><blockquote data-v-89cf40e3><p data-v-89cf40e3>First value:<br data-v-89cf40e3> 1: Communication Mode (via RS232)<br data-v-89cf40e3> 0: Normal Mode</p></blockquote><blockquote data-v-89cf40e3><p data-v-89cf40e3>Second value:<br data-v-89cf40e3> 1: Ignore cup-depart (Ice will be out even if the cup is departed)<br data-v-89cf40e3> 0: Stop the ice-out when the cup is departed</p></blockquote><p data-v-89cf40e3>Example :</p><div class="language- vp-adaptive-theme" data-v-89cf40e3><button title="Copy Code" class="copy" data-v-89cf40e3></button><span class="lang" data-v-89cf40e3></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-89cf40e3><code data-v-89cf40e3><span class="line" data-v-89cf40e3><span data-v-89cf40e3>icevan_set_mode(1, 1) # Set the operation mode of the ice machine to communication mode</span></span></code></pre></div></li></ul><h4 id="_3-3-icevan-set-timeout" tabindex="-1" data-v-89cf40e3>3.3 icevan_set_timeout(#) <a class="header-anchor" href="#_3-3-icevan-set-timeout" aria-label="Permalink to &quot;3.3 icevan_set_timeout(#)&quot;" data-v-89cf40e3>​</a></h4><ul data-v-89cf40e3><li data-v-89cf40e3><p data-v-89cf40e3>icevan_set_timeout(#)<br data-v-89cf40e3></p><p data-v-89cf40e3>Ice dispensing timeout setting (timeout when ice is not removed) (Ref. Icevan Manual Chap.6)</p><p data-v-89cf40e3>Input :</p><blockquote data-v-89cf40e3><p data-v-89cf40e3>Time (Unit: second)</p></blockquote><p data-v-89cf40e3>Example :</p><div class="language- vp-adaptive-theme" data-v-89cf40e3><button title="Copy Code" class="copy" data-v-89cf40e3></button><span class="lang" data-v-89cf40e3></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-89cf40e3><code data-v-89cf40e3><span class="line" data-v-89cf40e3><span data-v-89cf40e3>icevan_set_timeout(600)</span></span></code></pre></div></li></ul><h4 id="_3-4-icevan-set-airtemp" tabindex="-1" data-v-89cf40e3>3.4 icevan_set_airtemp(#, #) <a class="header-anchor" href="#_3-4-icevan-set-airtemp" aria-label="Permalink to &quot;3.4 icevan_set_airtemp(#, #)&quot;" data-v-89cf40e3>​</a></h4><ul data-v-89cf40e3><li data-v-89cf40e3><p data-v-89cf40e3>icevan_set_airtemp(#, #)<br data-v-89cf40e3></p><p data-v-89cf40e3>Set for the ambient temperature limit (lower/upper settings) (Ref. Icevan Manual Chap.8)</p><p data-v-89cf40e3>Input :</p><blockquote data-v-89cf40e3><p data-v-89cf40e3>First value : Lower temperature setting : (0~10)<br data-v-89cf40e3> Second value : Upper temperature setting : (40~70)</p></blockquote><p data-v-89cf40e3>Example :</p><div class="language- vp-adaptive-theme" data-v-89cf40e3><button title="Copy Code" class="copy" data-v-89cf40e3></button><span class="lang" data-v-89cf40e3></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-89cf40e3><code data-v-89cf40e3><span class="line" data-v-89cf40e3><span data-v-89cf40e3>icevan_set_airtemp(5, 65)</span></span></code></pre></div></li></ul><h4 id="_3-5-icevan-out" tabindex="-1" data-v-89cf40e3>3.5 icevan_out(#, #) <a class="header-anchor" href="#_3-5-icevan-out" aria-label="Permalink to &quot;3.5 icevan_out(#, #)&quot;" data-v-89cf40e3>​</a></h4><ul data-v-89cf40e3><li data-v-89cf40e3><p data-v-89cf40e3>icevan_out(#, #)<br data-v-89cf40e3></p><p data-v-89cf40e3>Most core function. Ice/Water output function (Ref. Icevan Manual Chap.2)</p><p data-v-89cf40e3>Input :</p><blockquote data-v-89cf40e3><p data-v-89cf40e3>First value : Time for ice-out (Unit: second)<br data-v-89cf40e3> Second value : Time for water-out (Unit: second)</p></blockquote><p data-v-89cf40e3>Example :</p><div class="language- vp-adaptive-theme" data-v-89cf40e3><button title="Copy Code" class="copy" data-v-89cf40e3></button><span class="lang" data-v-89cf40e3></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-89cf40e3><code data-v-89cf40e3><span class="line" data-v-89cf40e3><span data-v-89cf40e3>icevan_out(5.7, 0) # Make Ice during 5.7 second and water during 0 second</span></span></code></pre></div></li></ul><h4 id="_3-6-icevan-reset" tabindex="-1" data-v-89cf40e3>3.6 icevan_reset() <a class="header-anchor" href="#_3-6-icevan-reset" aria-label="Permalink to &quot;3.6 icevan_reset()&quot;" data-v-89cf40e3>​</a></h4><ul data-v-89cf40e3><li data-v-89cf40e3><p data-v-89cf40e3>icevan_reset()<br data-v-89cf40e3></p><p data-v-89cf40e3>Reset machine (Ref. Icevan Manual Chap.7)</p><p data-v-89cf40e3>Input :</p><blockquote data-v-89cf40e3><p data-v-89cf40e3>None</p></blockquote><p data-v-89cf40e3>Example :</p><div class="language- vp-adaptive-theme" data-v-89cf40e3><button title="Copy Code" class="copy" data-v-89cf40e3></button><span class="lang" data-v-89cf40e3></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-89cf40e3><code data-v-89cf40e3><span class="line" data-v-89cf40e3><span data-v-89cf40e3>icevan_reset()</span></span></code></pre></div></li></ul><h4 id="_3-7-icevan-clear" tabindex="-1" data-v-89cf40e3>3.7 icevan_clear() <a class="header-anchor" href="#_3-7-icevan-clear" aria-label="Permalink to &quot;3.7 icevan_clear()&quot;" data-v-89cf40e3>​</a></h4><ul data-v-89cf40e3><li data-v-89cf40e3><p data-v-89cf40e3>icevan_clear()<br data-v-89cf40e3></p><p data-v-89cf40e3>Clear all information (As explained by Ice Van manual, this function exists but doesn&#39;t work.)</p><p data-v-89cf40e3>Input :</p><blockquote data-v-89cf40e3><p data-v-89cf40e3>None</p></blockquote><p data-v-89cf40e3>Example :</p><div class="language- vp-adaptive-theme" data-v-89cf40e3><button title="Copy Code" class="copy" data-v-89cf40e3></button><span class="lang" data-v-89cf40e3></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-89cf40e3><code data-v-89cf40e3><span class="line" data-v-89cf40e3><span data-v-89cf40e3>icevan_clear()</span></span></code></pre></div></li></ul>',24),o=[n];function r(f,s,l,p,u,_){return e(),t("div",null,o)}const b=a(v,[["render",r],["__scopeId","data-v-89cf40e3"]]);export{m as __pageData,b as default};
