import{_ as a,c as t,o as e,U as d,AC as f,AD as i}from"./chunks/framework.fHC5UfPI.js";const m=JSON.parse('{"title":"Rainbow x Icevan","description":"","frontmatter":{"layout":"doc","outline":"deep"},"headers":[],"relativePath":"technical_docs/device_icevan.md","filePath":"technical_docs/device_icevan.md"}'),v={name:"technical_docs/device_icevan.md"},o=d('<h1 id="rainbow-x-icevan" tabindex="-1" data-v-f52f288e>Rainbow x Icevan <a class="header-anchor" href="#rainbow-x-icevan" aria-label="Permalink to &quot;Rainbow x Icevan&quot;" data-v-f52f288e>​</a></h1><h3 id="_1-configuration-setting" tabindex="-1" data-v-f52f288e>1. CONFIGURATION &amp; SETTING <a class="header-anchor" href="#_1-configuration-setting" aria-label="Permalink to &quot;1. CONFIGURATION &amp; SETTING&quot;" data-v-f52f288e>​</a></h3><p data-v-f52f288e><img src="'+f+'" alt="missing" data-v-f52f288e></p><ul data-v-f52f288e><li data-v-f52f288e>Connect a commercial USB-RS232 (USB-serial) device to the control box of Rainbow Robot</li><li data-v-f52f288e>Communication lines between devices should be cross-connected / Connect the GND (ground) wires on both sides. (Common ground)</li><li data-v-f52f288e>Icevan equipment uses serial communication speed/Baud rate as 9600 bps, so set the rainbow control box accordingly.</li></ul><p data-v-f52f288e><img src="'+i+'" alt="missing" data-v-f52f288e></p><h3 id="_2-variable-description" tabindex="-1" data-v-f52f288e>2. VARIABLE DESCRIPTION <a class="header-anchor" href="#_2-variable-description" aria-label="Permalink to &quot;2. VARIABLE DESCRIPTION&quot;" data-v-f52f288e>​</a></h3><div class="warning custom-block" data-v-f52f288e><p class="custom-block-title" data-v-f52f288e>WARNING</p><ul data-v-f52f288e><li data-v-f52f288e>In the table below, the information in the yellow area is the information transmitted by Icevan (ice machine).</li><li data-v-f52f288e>The values stored in the variables in the yellow area contain the information that the Icevan transmits to the Rainbow-ControlBox through communication.</li></ul></div><table data-v-f52f288e><tr data-v-f52f288e><th data-v-f52f288e>Variable Name</th><th data-v-f52f288e>Description</th><th data-v-f52f288e>Ref. Icevan Manual</th></tr><tr data-v-f52f288e><td class="bg-none" data-v-f52f288e>ICE_INFO_CONNECTED</td><td data-v-f52f288e>Whether it is communicating with the Icevan</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td class="bg-none" data-v-f52f288e>ICE_INFO_REQUESTING</td><td data-v-f52f288e>*RB developer only</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td class="bg-none" data-v-f52f288e>ICE_INFO_USING</td><td data-v-f52f288e>*RB developer only</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td class="bg-none" data-v-f52f288e>ICE_INFO_VECSIZE</td><td data-v-f52f288e>*RB developer only</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_MODE_COMM</td><td data-v-f52f288e>1:Communication mode / 0:Normal mode</td><td data-v-f52f288e>Chap. 1</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_MODE_CUP</td><td data-v-f52f288e>1:Ignore Cup depart / 0: Stop Ice-out if Cupdepart</td><td data-v-f52f288e>Chap. 1</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_TIME_ICE</td><td data-v-f52f288e>Last Ice-out time</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_TIME_WATER</td><td data-v-f52f288e>Last Water-out time</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_AMBI_LOW</td><td data-v-f52f288e>Lower limit for ambient temperature setting</td><td data-v-f52f288e>Chap. 4</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_AMBI_HIGH</td><td data-v-f52f288e>Upper limit for ambient temperature setting</td><td data-v-f52f288e>Chap. 4</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_TEMP_AMBI</td><td data-v-f52f288e>Temperature : Ambient</td><td data-v-f52f288e>Chap. 5</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_TEMP_EVAPO</td><td data-v-f52f288e>Temperature : Evaporator</td><td data-v-f52f288e>Chap. 5-1</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_INFO_TEMP_CONDEN</td><td data-v-f52f288e>Temperature : Condensor</td><td data-v-f52f288e>Chap. 5-1</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_LAST_ICE_NO</td><td data-v-f52f288e>If ice is not detected in the previous ice-out</td><td data-v-f52f288e>Manual: ignore this</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_LAST_ICE_YES</td><td data-v-f52f288e>If ice is detected in the previous ice-out</td><td data-v-f52f288e>Manual: ignore this</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_COMP_WORK</td><td data-v-f52f288e>Whether the compressor works (1=working)</td><td data-v-f52f288e>Page 3 upper table</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_MOTOR_WORK</td><td data-v-f52f288e>Whether the gear-motor works (1=working)</td><td data-v-f52f288e>Page 3 upper table</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_OUT_SOL</td><td data-v-f52f288e>Ice-out SOL detection (1=detected)</td><td data-v-f52f288e>Page 3 upper table</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_CUP_LEVEL</td><td data-v-f52f288e>Cup lever detection (1=detected)</td><td data-v-f52f288e>Page 3 upper table</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_COMM_MODE</td><td data-v-f52f288e>1 if communication mode</td><td data-v-f52f288e>Page 3 upper table</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_FULL_ICE</td><td data-v-f52f288e>Ice-Full signal (1 = Full Ice)</td><td data-v-f52f288e>Page 3 upper table</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_ERR_1</td><td data-v-f52f288e>*RB developer only</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_ERR_2</td><td data-v-f52f288e>*RB developer only</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_ERR_3</td><td data-v-f52f288e>*RB developer only</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_ERR_4</td><td data-v-f52f288e>*RB developer only</td><td data-v-f52f288e></td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_ERR_CODE</td><td data-v-f52f288e>Error code (0 = Normal)</td><td data-v-f52f288e>Page 3 lower table</td></tr><tr data-v-f52f288e><td data-v-f52f288e>ICE_STATE_RD</td><td data-v-f52f288e>1=Ready to IceOut / 0=IceOut done or Timeout</td><td data-v-f52f288e>Page 3 middle table</td></tr></table><h3 id="_3-function-description" tabindex="-1" data-v-f52f288e>3. FUNCTION DESCRIPTION <a class="header-anchor" href="#_3-function-description" aria-label="Permalink to &quot;3. FUNCTION DESCRIPTION&quot;" data-v-f52f288e>​</a></h3><br data-v-f52f288e><h4 id="_3-1-icevan-update-start" tabindex="-1" data-v-f52f288e>3.1 icevan_update_start(#) <a class="header-anchor" href="#_3-1-icevan-update-start" aria-label="Permalink to &quot;3.1 icevan_update_start(#)&quot;" data-v-f52f288e>​</a></h4><ul data-v-f52f288e><li data-v-f52f288e><p data-v-f52f288e>icevan_update_start(#)<br data-v-f52f288e></p><p data-v-f52f288e>Start requesting status/setting information from the ice machine</p><p data-v-f52f288e>Input :</p><blockquote data-v-f52f288e><p data-v-f52f288e>1: Data request Start<br data-v-f52f288e> 0: Data request End</p></blockquote><p data-v-f52f288e>Example :</p><div class="language- vp-adaptive-theme" data-v-f52f288e><button title="Copy Code" class="copy" data-v-f52f288e></button><span class="lang" data-v-f52f288e></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-f52f288e><code data-v-f52f288e><span class="line" data-v-f52f288e><span data-v-f52f288e>icevan_update_start(1)</span></span></code></pre></div></li></ul><h4 id="_3-2-icevan-set-mode" tabindex="-1" data-v-f52f288e>3.2 icevan_set_mode(#, #) <a class="header-anchor" href="#_3-2-icevan-set-mode" aria-label="Permalink to &quot;3.2 icevan_set_mode(#, #)&quot;" data-v-f52f288e>​</a></h4><ul data-v-f52f288e><li data-v-f52f288e><p data-v-f52f288e>icevan_set_mode(#, #)<br data-v-f52f288e></p><p data-v-f52f288e>Select the operation mode of the ice machine (Ref. Icevan Manual Chap.1)</p><p data-v-f52f288e>Input :</p><blockquote data-v-f52f288e><p data-v-f52f288e>First value:<br data-v-f52f288e> 1: Communication Mode (via RS232)<br data-v-f52f288e> 0: Normal Mode</p></blockquote><blockquote data-v-f52f288e><p data-v-f52f288e>Second value:<br data-v-f52f288e> 1: Ignore cup-depart (Ice will be out even if the cup is departed)<br data-v-f52f288e> 0: Stop the ice-out when the cup is departed</p></blockquote><p data-v-f52f288e>Example :</p><div class="language- vp-adaptive-theme" data-v-f52f288e><button title="Copy Code" class="copy" data-v-f52f288e></button><span class="lang" data-v-f52f288e></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-f52f288e><code data-v-f52f288e><span class="line" data-v-f52f288e><span data-v-f52f288e>icevan_set_mode(1, 1) # Set the operation mode of the ice machine to communication mode</span></span></code></pre></div></li></ul><h4 id="_3-3-icevan-set-timeout" tabindex="-1" data-v-f52f288e>3.3 icevan_set_timeout(#) <a class="header-anchor" href="#_3-3-icevan-set-timeout" aria-label="Permalink to &quot;3.3 icevan_set_timeout(#)&quot;" data-v-f52f288e>​</a></h4><ul data-v-f52f288e><li data-v-f52f288e><p data-v-f52f288e>icevan_set_timeout(#)<br data-v-f52f288e></p><p data-v-f52f288e>Ice dispensing timeout setting (timeout when ice is not removed) (Ref. Icevan Manual Chap.6)</p><p data-v-f52f288e>Input :</p><blockquote data-v-f52f288e><p data-v-f52f288e>Time (Unit: second)</p></blockquote><p data-v-f52f288e>Example :</p><div class="language- vp-adaptive-theme" data-v-f52f288e><button title="Copy Code" class="copy" data-v-f52f288e></button><span class="lang" data-v-f52f288e></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-f52f288e><code data-v-f52f288e><span class="line" data-v-f52f288e><span data-v-f52f288e>icevan_set_timeout(600)</span></span></code></pre></div></li></ul><h4 id="_3-4-icevan-set-airtemp" tabindex="-1" data-v-f52f288e>3.4 icevan_set_airtemp(#, #) <a class="header-anchor" href="#_3-4-icevan-set-airtemp" aria-label="Permalink to &quot;3.4 icevan_set_airtemp(#, #)&quot;" data-v-f52f288e>​</a></h4><ul data-v-f52f288e><li data-v-f52f288e><p data-v-f52f288e>icevan_set_airtemp(#, #)<br data-v-f52f288e></p><p data-v-f52f288e>Set for the ambient temperature limit (lower/upper settings) (Ref. Icevan Manual Chap.8)</p><p data-v-f52f288e>Input :</p><blockquote data-v-f52f288e><p data-v-f52f288e>First value : Lower temperature setting : (0~10)<br data-v-f52f288e> Second value : Upper temperature setting : (40~70)</p></blockquote><p data-v-f52f288e>Example :</p><div class="language- vp-adaptive-theme" data-v-f52f288e><button title="Copy Code" class="copy" data-v-f52f288e></button><span class="lang" data-v-f52f288e></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-f52f288e><code data-v-f52f288e><span class="line" data-v-f52f288e><span data-v-f52f288e>icevan_set_airtemp(5, 65)</span></span></code></pre></div></li></ul><h4 id="_3-5-icevan-out" tabindex="-1" data-v-f52f288e>3.5 icevan_out(#, #) <a class="header-anchor" href="#_3-5-icevan-out" aria-label="Permalink to &quot;3.5 icevan_out(#, #)&quot;" data-v-f52f288e>​</a></h4><ul data-v-f52f288e><li data-v-f52f288e><p data-v-f52f288e>icevan_out(#, #)<br data-v-f52f288e></p><p data-v-f52f288e>Most core function. Ice/Water output function (Ref. Icevan Manual Chap.2)</p><p data-v-f52f288e>Input :</p><blockquote data-v-f52f288e><p data-v-f52f288e>First value : Time for ice-out (Unit: second)<br data-v-f52f288e> Second value : Time for water-out (Unit: second)</p></blockquote><p data-v-f52f288e>Example :</p><div class="language- vp-adaptive-theme" data-v-f52f288e><button title="Copy Code" class="copy" data-v-f52f288e></button><span class="lang" data-v-f52f288e></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-f52f288e><code data-v-f52f288e><span class="line" data-v-f52f288e><span data-v-f52f288e>icevan_out(5.7, 0) # Make Ice during 5.7 second and water during 0 second</span></span></code></pre></div></li></ul><h4 id="_3-6-icevan-reset" tabindex="-1" data-v-f52f288e>3.6 icevan_reset() <a class="header-anchor" href="#_3-6-icevan-reset" aria-label="Permalink to &quot;3.6 icevan_reset()&quot;" data-v-f52f288e>​</a></h4><ul data-v-f52f288e><li data-v-f52f288e><p data-v-f52f288e>icevan_reset()<br data-v-f52f288e></p><p data-v-f52f288e>Reset machine (Ref. Icevan Manual Chap.7)</p><p data-v-f52f288e>Input :</p><blockquote data-v-f52f288e><p data-v-f52f288e>None</p></blockquote><p data-v-f52f288e>Example :</p><div class="language- vp-adaptive-theme" data-v-f52f288e><button title="Copy Code" class="copy" data-v-f52f288e></button><span class="lang" data-v-f52f288e></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-f52f288e><code data-v-f52f288e><span class="line" data-v-f52f288e><span data-v-f52f288e>icevan_reset()</span></span></code></pre></div></li></ul><h4 id="_3-7-icevan-clear" tabindex="-1" data-v-f52f288e>3.7 icevan_clear() <a class="header-anchor" href="#_3-7-icevan-clear" aria-label="Permalink to &quot;3.7 icevan_clear()&quot;" data-v-f52f288e>​</a></h4><ul data-v-f52f288e><li data-v-f52f288e><p data-v-f52f288e>icevan_clear()<br data-v-f52f288e></p><p data-v-f52f288e>Clear all information (As explained by Ice Van manual, this function exists but doesn&#39;t work.)</p><p data-v-f52f288e>Input :</p><blockquote data-v-f52f288e><p data-v-f52f288e>None</p></blockquote><p data-v-f52f288e>Example :</p><div class="language- vp-adaptive-theme" data-v-f52f288e><button title="Copy Code" class="copy" data-v-f52f288e></button><span class="lang" data-v-f52f288e></span><pre class="shiki shiki-themes github-light github-dark vp-code" data-v-f52f288e><code data-v-f52f288e><span class="line" data-v-f52f288e><span data-v-f52f288e>icevan_clear()</span></span></code></pre></div></li></ul>',24),n=[o];function r(s,c,l,p,u,_){return e(),t("div",null,n)}const b=a(v,[["render",r],["__scopeId","data-v-f52f288e"]]);export{m as __pageData,b as default};
